mapper.update:

	UPDATE BOARD 
		SET READ_COUNT = READ_COUNT+1 
	WHERE BOARD_ID=#{boardId}

mapper.select:

	SELECT PASSWORD 
	FROM BOARD 
	WHERE BOARD_ID=#{boardId}

mapper.update:

 	UPDATE 
 		BOARD_UPLOAD_FILE 
 	SET 
 		FILE_NAME=#{fileName}, FILE_SIZE=#{fileSize}, FILE_CONTENT_TYPE=#{fileContentType}, FILE_DATA=#{fileData} 
 	WHERE 
 		FILE_ID=#{fileId}

mapper.select:

	SELECT COUNT(BOARD_ID) AS "count"
	FROM BOARD 
	WHERE CATEGORY_ID=#{categoryId}
